import React, { useEffect, useState } from "react";
import api from "../api";

function Home() {
  const [questions, setQuestions] = useState([]);  // SAFE EMPTY ARRAY
  const [error, setError] = useState("");

  useEffect(() => {
    api.get("/questions")
      .then((res) => setQuestions(res.data))
      .catch((err) => {
        console.error(err);
        setError("Failed to load questions.");
      });
  }, []);

  return (
    <div>
      <h1>Trivia Game</h1>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <ul>
        {questions.map((q) => (
          <li key={q._id}>{q.question}</li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
