import React, { useState, useEffect } from "react";
import api from "../api";

export default function Home() {
  const [questions, setQuestions] = useState([]);
  const [shuffledQuestions, setShuffledQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [showGame, setShowGame] = useState(false);
  const [selected, setSelected] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch questions from backend
  const loadQuestions = async () => {
    try {
      setLoading(true);
      const res = await api.get("/questions");
      setQuestions(res.data);

      // shuffle questions
      const shuffled = [...res.data].sort(() => Math.random() - 0.5);
      setShuffledQuestions(shuffled);

      setLoading(false);
    } catch (error) {
      console.error(error);
      alert("Error loading questions. Check backend connection.");
      setLoading(false);
    }
  };

  // Start game
  const startGame = () => {
    loadQuestions();
    setShowGame(true);
    setCurrent(0);
    setScore(0);
    setSelected(null);
  };

  // Handle answer selection
  const chooseAnswer = (choice) => {
    setSelected(choice);

    const correct = shuffledQuestions[current].answer;

    if (choice === correct) {
      setScore(prev => prev + 1);
    }

    setTimeout(() => {
      setSelected(null);
      setCurrent(prev => prev + 1);
    }, 800);
  };

  // Restart
  const restart = () => {
    setShowGame(false);
    setQuestions([]);
    setShuffledQuestions([]);
    setCurrent(0);
    setScore(0);
  };

  if (!showGame) {
    return (
      <div style={styles.container}>
        <h1>Trivia Game</h1>
        <button style={styles.startBtn} onClick={startGame}>
          Start Game
        </button>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={styles.container}>
        <h2>Loading Questions...</h2>
      </div>
    );
  }

  // Game Finished
  if (current >= shuffledQuestions.length) {
    return (
      <div style={styles.container}>
        <h1>Game Over!</h1>
        <h2>Your Score: {score} / {shuffledQuestions.length}</h2>
        <button style={styles.startBtn} onClick={restart}>
          Play Again
        </button>
      </div>
    );
  }

  const q = shuffledQuestions[current];

  return (
    <div style={styles.container}>
      <h2>Question {current + 1} / {shuffledQuestions.length}</h2>
      <h3 style={styles.question}>{q.question}</h3>

      <div style={styles.choicesBox}>
        {q.choices.map((choice, index) => {
          const correct = q.answer;

          // dynamic colors
          let bg = "#eee";

          if (selected) {
            if (choice === correct) bg = "lightgreen";
            else if (choice === selected) bg = "salmon";
          }

          return (
            <button
              key={index}
              style={{ ...styles.choiceBtn, backgroundColor: bg }}
              onClick={() => chooseAnswer(choice)}
              disabled={!!selected}
            >
              {choice}
            </button>
          );
        })}
      </div>
    </div>
  );
}

// ------------------ Styles ------------------

const styles = {
  container: {
    textAlign: "center",
    padding: "40px",
    maxWidth: "600px",
    margin: "auto",
  },
  startBtn: {
    padding: "15px 30px",
    fontSize: "18px",
    cursor: "pointer",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "8px",
  },
  question: {
    marginBottom: "20px",
  },
  choicesBox: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  choiceBtn: {
    padding: "12px",
    border: "1px solid #ccc",
    borderRadius: "6px",
    fontSize: "16px",
    cursor: "pointer",
  },
};
