import React, { useEffect, useState } from "react";
import api from "../api";

export default function Home() {
  const [questions, setQuestions] = useState([]); // Always an array, never undefined
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showQuestion, setShowQuestion] = useState(false);
  const [selected, setSelected] = useState(null);
  const [loading, setLoading] = useState(false);
  const [score, setScore] = useState(0);

  const fetchQuestions = async () => {
    try {
      setLoading(true);
      const response = await api.get("/questions");
      setQuestions(response.data || []); 
      setLoading(false);
    } catch (error) {
      console.error("Error fetching questions:", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  const startGame = () => {
    if (questions.length === 0) return;
    setShowQuestion(true);
    setCurrentIndex(0);
    setScore(0);
    setSelected(null);
  };

  const nextQuestion = () => {
    if (currentIndex + 1 < questions.length) {
      setCurrentIndex(currentIndex + 1);
      setSelected(null);
    } else {
      alert(`Game over! Your score: ${score}/${questions.length}`);
      setShowQuestion(false);
    }
  };

  const chooseAnswer = (choice) => {
    setSelected(choice);

    if (choice === questions[currentIndex].answer) {
      setScore(score + 1);
    }

    setTimeout(() => {
      nextQuestion();
    }, 1000);
  };

  if (loading) return <h2 style={{ textAlign: "center" }}>Loading...</h2>;

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Trivia Game</h1>

      {!showQuestion && (
        <button
          onClick={startGame}
          style={{
            padding: "12px 20px",
            fontSize: "18px",
            borderRadius: "10px",
            border: "none",
            background: "#007bff",
            color: "white",
            cursor: "pointer",
          }}
        >
          Start Game
        </button>
      )}

      {showQuestion && questions.length > 0 && (
        <div
          style={{
            marginTop: "30px",
            padding: "20px",
            maxWidth: "500px",
            marginInline: "auto",
            border: "2px solid #ccc",
            borderRadius: "12px",
            background: "#f9f9f9",
          }}
        >
          <h2>{questions[currentIndex].question}</h2>

          <div style={{ marginTop: "20px" }}>
            {questions[currentIndex].choices.map((choice, index) => (
              <button
                key={index}
                onClick={() => chooseAnswer(choice)}
                style={{
                  display: "block",
                  width: "100%",
                  margin: "8px 0",
                  padding: "12px",
                  fontSize: "16px",
                  borderRadius: "8px",
                  border: "1px solid #888",
                  cursor: "pointer",
                  background:
                    selected === choice
                      ? choice === questions[currentIndex].answer
                        ? "lightgreen"
                        : "#ffb3b3"
                      : "white",
                }}
              >
                {choice}
              </button>
            ))}
          </div>

          <p style={{ marginTop: "20px" }}>
            Question {currentIndex + 1} / {questions.length}
          </p>
        </div>
      )}
    </div>
  );
}
