import { useState, useEffect } from "react";
import api from "../api";

export default function Home() {
  const [gameStarted, setGameStarted] = useState(false);
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [selected, setSelected] = useState(null);
  const [showAnswer, setShowAnswer] = useState(false);

  // Load questions when game starts
  useEffect(() => {
    if (gameStarted) {
      api.get("/questions").then((res) => {
        setQuestions(res.data);
      });
    }
  }, [gameStarted]);

  function handleStart() {
    setGameStarted(true);
  }

  function chooseAnswer(choice) {
    if (showAnswer) return;
    setSelected(choice);
    setShowAnswer(true);
  }

  function nextQuestion() {
    setSelected(null);
    setShowAnswer(false);
    setCurrent((prev) => prev + 1);
  }

  // Loading screen
  if (gameStarted && questions.length === 0) {
    return <h2 style={{ textAlign: "center" }}>Loading questions...</h2>;
  }

  // Game finished
  if (gameStarted && current >= questions.length) {
    return (
      <div style={styles.center}>
        <h2>ðŸŽ‰ You finished the trivia!</h2>
        <button style={styles.button} onClick={() => window.location.reload()}>
          Play Again
        </button>
      </div>
    );
  }

  // Before starting the game
  if (!gameStarted) {
    return (
      <div style={styles.center}>
        <h1>Trivia Game</h1>
        <button style={styles.button} onClick={handleStart}>
          Start Game
        </button>
      </div>
    );
  }

  // During the game
  const q = questions[current];

  return (
    <div style={styles.container}>
      <h2>{q.question}</h2>

      <div style={styles.choices}>
        {q.choices.map((choice, i) => {
          const isCorrect = choice === q.answer;
          const isSelected = choice === selected;

          return (
            <button
              key={i}
              onClick={() => chooseAnswer(choice)}
              style={{
                ...styles.choiceButton,
                backgroundColor: showAnswer
                  ? isCorrect
                    ? "#4CAF50"
                    : isSelected
                    ? "#E74C3C"
                    : "white"
                  : "white",
              }}
            >
              {choice}
            </button>
          );
        })}
      </div>

      {showAnswer && (
        <button style={styles.nextButton} onClick={nextQuestion}>
          Next
        </button>
      )}
    </div>
  );
}

const styles = {
  center: {
    textAlign: "center",
    marginTop: "120px",
  },
  button: {
    padding: "14px 28px",
    marginTop: "20px",
    fontSize: "18px",
    backgroundColor: "#3498db",
    border: "none",
    color: "white",
    borderRadius: "8px",
    cursor: "pointer",
  },
  container: {
    maxWidth: "600px",
    margin: "80px auto",
    padding: "20px",
    textAlign: "center",
  },
  choices: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
    marginTop: "20px",
  },
  choiceButton: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    cursor: "pointer",
    transition: "0.3s",
  },
  nextButton: {
    padding: "12px 24px",
    marginTop: "20px",
    fontSize: "18px",
    border: "none",
    borderRadius: "8px",
    backgroundColor: "#2ecc71",
    color: "white",
    cursor: "pointer",
  },
};
